public class d49 extends KeEntry{
	public d49() {
        set(1, new LiuReKe("癸丑日第一局　干上丑", "课体：伏吟，乱首，励德，游子，稼穑。", "课义：见财动心，凶祸来侵，中传虎鬼，仕宦宜临。", "解曰：末未遁丁，暗财也。三传纯土，化作鬼矣。若取此财，凶祸不小。中传鬼乘白虎，常人遇之，病讼可畏。惟仕宦则为催官符至，上任极速，故宜临也。", "断曰：伏吟有郁塞不舒之情，稼穑有艰难稽滞之象。幸癸日名脱难煞，否极泰来，久厄者反有解散之意。支作破碎，临干克干发用，谓之上门乱首，主卑下犯上，事发于外而起于内。水日逢丁，必主财动，及远方封寄财物。未娶则结缡可贺，已娶则离别堪忧。如行年上神，克去丁所乘神，又非此论。", "天时：晴而有云。家宅：卑下有犯上之事，须防病讼刑伤。功名：催官符至，得官甚速。求财：可得，但不可多取。婚姻：不吉。胎产：二阴包阳，孕男，产迟。疾病：闭口卦，饮食不进，甚凶，主往妻家而得病，如年命有卯木可救，乘寅木必得神护。寻访：必出外干事，先允许，后必改。出行：有阻。行人：即回。失脱：家内寻可获。盗逃：不远。兵战：宜慎。", "毕法云：宾主不投刑在上，水日逢丁财动之，任信丁马须言动。玉成歌云：斩关游子身当动。纂要云：旬尾在干作初，宜闭门以免祸。纂义云：支干共处，混同无别，婚姻不明，事难剖决。"));
        set(0, new LiuReKe("癸丑日第二局　干上子", "课体：重审，退茹。", "课义：旺禄可用，子丑合共，牛女婚宜，公讼动众。", "解曰：旺禄临身，昼将青龙，大可守而用之。发用子加丑迭合，谓之牛女相会，婚姻最利。但末传之戌，昼夜俱乘白虎，如占公讼，则必干动众人也。", "断曰：重审之卦，事每不顺。三传退茹，主有逆事相牵。宜慎其始，以绝祸端。魁度天门，又为失友格，人情失好。凡占阻隔，有孟行不行之象。旺禄自干支上发用，中乘驿马，木得催官符，仕人占此，必莅任速而迁转频也。", "天时：天罡指阴，传课皆水，必雨。家宅：干支皆乘旺禄，宅旺人亨。功名：传中禄马交驰，末得催官使者，大吉，夜占防罚俸。求财：不能。婚姻：昼占龙乘旺禄，后化得合，男主贵而富，女主美而贤，夜占亦吉。胎产：二阳包阴，孕女，胎上乘绝，产速。疾病：病在肾经，症恐复发。谒贵：昼占大利。争讼：恐防覆审。出行：利有攸往。行人：归迟。捕获：昼占贼在西南路旁饭店，有女人掌柜。兵战：凯歌可奏。", "毕法云：旺禄临身徒妄作，权摄不正禄临支，两蛇夹墓凶难免。邵南妙经云：天乙临德上，谓之贵人临天德。占岁，主布泽天下，国有恩赦，常人有喜事变通。纂义云：青龙入海，财禄自然，审其取舍，福自绵绵。"));
        set(11, new LiuReKe("癸丑日第三局　干上亥", "课体：重审，三奇，时遁。", "课义：行人出外，携财而至，未与巳酉，难为亥类。", "解曰：亥马自干发用，丁未居于末传而为财，迤逦复生回于，故主出外得财而归也。但中传之酉，旬遁为己，未又元遁为己，俱克亥水。且癸水败于酉，凡占同类之事，皆非所宜。", "断曰：重审之卦，以卑犯尊，传课纯阴，利营私干。干支乘亥自刑发用，将值空勾。凡百交接，防有欺诈。幸为三奇，逢凶化吉，占者吉凶，俱于卯日克应。", "天时：传课纯阴，主有小雨。家宅：马乘勾空，入宅临干，防有军吏欺诳。功名：官爻立败地，青龙入空乡，不利。求财：水日逢丁，可以如意。婚姻：日辰各乘自刑，不宜。胎产：孕主男，产防厄。疾病：肾经膀胱之疾，不妨。争讼：卯月日可结。投谒：宾主不投。失脱：难得。出行：马作自刑，未能顺遂，初遭夹克，恐不由己。行人：辰戌日至。捕获：昼占贼在南马房，有女人作线窝脏。兵战：不吉。", "毕法云：宾主不投刑在上，水日逢丁财动之。观月经云：旺孕卦，不干传课事，只看夫妻年命上旺相。如七月未时占，妻命属金三十四，夫命属水三十七。夫年寅上子乃水旺，妻年亥上酉值秋旺，二旺则当怀孕，其他夫妻行年仿此。"));
        set(10, new LiuReKe("癸丑日第四局　干上戌", "课体：元首，稼穑，游子，斩关。", "课义：谋财祸攻，昼将弥凶，宜占官职，喜事重逢。", "解曰：癸以丁为财，奈三传皆土，类聚为鬼，取必有祸。昼占将乘蛇虎，其凶更甚。然干上戌虎发用，仕宦逢之为催官符至，则主重重见喜，以官爻重叠故也。", "断曰：元首之卦，以尊制卑，万事顺利。但稼穑乃艰难之兆，斩关有震动之形。传课全鬼，或冲或刑，昼将复乘蛇虎，若常人占此，不能免于惊忧。幸末传辰墓为戌虎冲开，势虽汹涌，而守正待时亦得终吉也。", "天时：传课皆土，主阴霾。家宅：主家破人离，凶殃叠见，宜禳之。功名：禄虽空，幸官星甚旺，可望。求财：恐有破散。婚姻：不宜。胎产：孕主女，产可忧。疾病：肝肾二经之病甚凶，宜禳之。争讼：势同骑虎，两败俱伤。出行：夜占吉。行人：即回。捕获：昼占在东南，夜占在正南。兵战：不利。坟葬：龙虎冲克，不吉。", "毕法云：虎临干鬼凶速速，水日逢丁财动之，全鬼化财财险危。集义云：白虎乘戌，临丑被刑，主官事吏人追呼。玉成歌云：斩关游子身当动。课经云：三阴如癸丑日，子将卯时申生人，行年在丑，天乙逆行，日辰在后，戌乘虎加癸为用，卯木克行年，三传戌未辰为三阴，凡事不通，多有晦滞。"));
        set(9, new LiuReKe("癸丑日第五局　干上酉", "课体：元首，从革，励德。", "课义：将克传生，己酉败人，宅衰人盛，惟便缙绅。", "解曰：天将纯土克身，全传会金生日，是将克传生也。癸水败于酉，而又遁己土，明败暗攻，是己酉败人也。三传脱支生干，主人口盛而宅舍衰。然支系官爻，如用昼将，并来生传，传递生干，乃官印相生，迁官进秩，岂非缙绅之便乎。", "断曰：从革之卦，有革故鼎新之象。事必关众，凡谋有阻，亦主变换不常。干支上酉为败气，亦为自刑，主彼我各相疑忌。幸德合发传，会成三合。课主和谐，可因贵以致富，亦名回环。吉则成吉，凶亦成凶。", "天时：晴占则阴，雨占则晴，家宅：干支皆败，防有奸私讼事牵连及长上之灾。功名：大利，夜占帘作德发用，试必高中。求财：得恐复失。婚姻：不宜：胎产：纯阴主男，产速而易。疾病：因酒食而得，或肝肾二经之病，可救。争讼：得理，昼占防刑。出行：须防乐里生悲。行人：未必得意。捕获：不能得。兵战：不宜。", "毕法云：干支皆败势倾颓，宾主不投刑在上，苦去甘来乐里悲。课经云：将克传生，虽有面前之生，背后亦防相害。谚云：贪他一粒粟，失却半年粮，即乐里生悲之谓也。"));
        set(8, new LiuReKe("癸丑日第六局　干上申", "课体：重审，斫轮，四绝。", "课义：没溺财爻，朽木难雕，两贵常怒，守旧逍遥。", "解曰：巳乃财爻，入于鬼墓之乡，所以没溺也。卯系旬空，是为朽木加申发用，何可雕也。昼巳入狱，夜卯受制，故两贵常怒。独干上申为长生，若能守旧，却得逍遥。虽乘元虎，化为金水一类，即有惊耗，亦无足虑。", "断曰：重审之卦，不可妄行。课体四绝，止宜结绝旧事，不利图新。斫轮卯空，须改科易业。另作营生，发用夜贵内战，必因贵人以滋口舌。中传官鬼，落陷受克。末传财爻，复被墓脱，主忧喜皆虚，吉凶无据。", "天时：天罡指阴，旺龙升天，主雨。家宅：长生临支加干，人宅安宁，将乘元虎，防有虚惊小耗。功名：不利。求财：财入库，不可得。婚姻：不成。胎产：二阴包阳，孕男产迟。疾病：肾水不足，得贵人文士医之可愈。争讼：虽凶有解。出行：马入墓，不吉，亦不成行。行人：不至。捕获：不得，兵战：不利。", "毕法云：朽木难雕别作为，三传递生人举荐。观月经云：斫轮团圆象，本自太冲生，求官必得禄，逢事得均平。邵南妙经云：天乙临德上，国家主有恩赦，布泽天下，常人有喜事变通。"));
        set(7, new LiuReKe("癸丑日第七局　干上未", "课体：反吟，稼穑，励德，游子。", "课义：四丁临未，刃蜜难舐，贵人休干，美中不美。", "解曰：干支初末四未遁丁皆财，财上带鬼，如舐刃蜜，舌必伤也。巳临亥，卯临酉，昼夜俱受克，故休干贵。此占凡事美中不美。", "断曰：反吟之卦，事主反覆。稼穑主于艰难，励德主于摇动，有孟静不静之象。传课皆鬼，止宜携财告贵，不可贪财。惟年命有申酉者，斯能获福。", "天时：主阴。家宅：人宅动摇，须防口舌官非；二月夜占，有外孝服。功名：大利纳粟奏名。求财：不得。婚姻：不成。胎产：孕男，产防不育。疾病：宜祷。争讼：彼此两伤。出行：甚利。行人：未回。捕获：去远难获。兵战：主客皆不利。", "毕法云：两贵受克难干贵。指南：卯将酉时占生产，曰：纯阴返阳，支上神与支相比，男子之祥。但胎神夹克无气，卯为子宿，受酉冲克，主卯年不育，果验。古鉴：戊申十二月丑将未时占赴任。曰：旧政上又见旧政，第六年方得赴任，君其终此任矣。第八年当以丧归。盖戊申年占，未乃申前已往之位，故名为旧。初末日辰皆未，上有朱雀文书，故曰旧政。六年者，未带丁，六数也。满盘是鬼，丑为墓田，乘太常为亡化，丑未八数，故断其第八年扶丧而归，悉验。"));
        set(6, new LiuReKe("癸丑日第八局　干上午", "课体：重审。", "课义：午系财类，来往受制，妻病财亡，难就难弃。", "解曰：午为癸妻之财，临干发用，既被癸伤，又遭夹克。及归本家，亥水先据，是往来受制，所以妻病财亡，弃就之间，不綦难哉。", "断曰：重审之卦，利在后动，初遭夹克，财不由己。末墓乘后，事多暗昧。传课自刑，惊忧难免。惟宜谨凛冰渊，庶几获福。", "天时：阴晴不定。家宅：人宅不安，防失财虚惊。功名：不利。求财：不得。婚姻：不吉。胎产：二阳包阴，孕女。疾病：心病或眼目疾，有解。争讼：干支上皆自刑，不利。出行：不利。行人：归而复出。捕获：易获。兵战：不宜。", "毕法云：前后逼迫难进退，宾主不投刑在上。断验：亥加午占家宅，曰：宅后西北水沟不通，致人常患眼日、肠气、痢泻之疾。若不速开，必主妇人颠狂。盖三传辰壅亥，亥克午，午临身宅害宅，故因宅以致害人也。又姜姓占前程，曰：初传午被癸克，妇人常患心血不宁。中传亥为同类，临于屋上，主为屋争讼。末传墓作天后，主妇人血气之疾。自身随传入墓，必乾艮上坟墓泥水浸棺，故前程不利。九年当有心疾，四年同族争屋，又五年若不动移，必死。盖午为心、为目、为妻、为屋，受癸之克，回家又有亥克，往来皆被其侵挠故也，果验。"));
        set(5, new LiuReKe("癸丑日第九局　干上巳", "课体：涉害，从革。", "课义：人盛宅狭，彼恶己益，众语来生，官应显赫。", "解曰：巳酉丑金局，脱支生干。占人宅，则人盛宅狭也。占彼己，则己益彼损也。昼贵皆土，将生传，传复生日，当有众人称扬。仕宦占此，则天将为官，三合为印，权柄双全，故宫应显赫。", "断曰：见机作事迟留，从革主多变迁。贵德临干入宅，宫印迤逦相生，主同心一德。然常人止宜倚贵营生，不可恃势妄行。", "天时：天罡指阳，主晴。家宅：传脱支生干，主宅破碎逼窄，人口兴旺。功名：有官有印，得人保举。求财：大利。婚姻：干支皆乘德神，传又三合递生，大吉。胎产：胎坐长生，大宜占孕，不利占产。疾病：心肾二经之症，或齿疼喉痛，不妨。争讼：无事。出行：水陆皆吉。行人：在途。捕获：可得。兵战：不利。", "毕法云：三传递生人举荐。古鉴：戊申八月巳加丑占家宅，曰：传酉八月正旺，人盛而亨。丑支生传泄气，宅狭而替。癸水见酉为酒，巳为厨，四年内必因修造酒店，女子死酒缸中。又巳为店业，传课三巳，店共三所，先开二店发财，后开一店却败，盖水绝于巳也。酉为锅镬，干支上巳为四，二四得八，八年内面前淘屋，必主停丧。其家亦作四分矣。丑为八月死气，加于妻位，主死女子，皆验。"));
        set(4, new LiuReKe("癸丑日第十局　干上辰", "课体：元首，稼穑，六仪，斩关，游子。", "课义：鬼贼纷纷，凶不可闻，纵寅卯命，亦作妖氛。", "解曰：一癸水而敌众土，凶不可言。纵寅卯作占人年命，似能制土。然彼先盗干气，亦作妖氛而已，安能为救哉。", "断曰：元首之卦，本为顺征。发用六仪，更多喜庆。斩关游子，皆有动象。传课皆官，末乘龙虎，仕宦逢此，谓之催官符至，上任极速。常占则罡墓覆日克日，夜又乘蛇，凶莫大焉。幸末戌乘虎冲辰，谓之破墓冲鬼，以凶制凶，犹可解救。应主先凶后吉，如用昼贵，名贵塞鬼户，鬼贼不出。凡有谋为，可无阻碍也，惟营私冒进，则有不利，即年命在寅者亦然。", "天时：阴云之象。家宅：仪神入宅，临干发用，主逢凶化吉。功名：末得催官，大吉。求财：寅卯月日，或寅卯生人可得。婚姻：不宜。胎产：阳包阴，主女，产不易。疾病：肾家之症，势甚危险。争讼：遭刑甚凶。出行：所至大吉。行人：墓覆，不回。捕获：夜占在东方舟车中。兵战：不利。坟葬：穴情水法俱好，却不发场。", "毕法云：二贵皆空虚喜期，干墓并关人宅废，催官使者赴官期。玉成歌云：斩关游子身当动。五十五占云：支上见干墓克干者，宅中鬼必伤人，若克行年，立见灾来。"));
        set(3, new LiuReKe("癸丑日第十一局　干上卯", "课体：元首，不备，间传，励德，迎阳。", "课义：拟贵告贵，事致委靡，现在财亡，遁丁何济。", "解曰：初卯夜贵，中巳昼贵，末又巳乡。经曰：课传俱贵转无依，主事多委托。所谓一国三公，十羊九牧，事不归一也。巳火为财，既落空亡，是现在之财，已不得用。未虽遁丁，乘土化鬼，又何济乎。", "断曰：元首之卦，顺利无咎。但课名不备，事必不周。且卯为死气脱干，迤逦脱去，谓之流消其源，枝断其根。凡事暗里损坏，徒费筹画，惟僧道宜之。贵立二八为励德，门户当有动摇。凡有谋为，急就则吉，迟缓便凶。盖以格合盈阳，阳已至盈，物极必反也。", "天时：盈阳格，主晴。家宅：人宅皆乘死神，只宜休息，不利动谋。功名：遍地贵人，反为不贵。求财：不得。婚姻：可称佳偶，但恐不成。胎产：传课纯阴，胎上属阳，男孕，产易。疾病：心肾二经之病，干支皆乘死气，初病无妨，久病凶。争讼：虽凶可解。出行：支干皆空，未必成行，去亦无利。行人：将返。捕获：昼占在东南，夜占在正北。兵战：有失众之虞。", "毕法云：课传俱贵转无依，昼夜贵加求两贵，二贵皆空虚喜期，人宅皆死各衰羸。指掌赋云：卯巳未为迎阳，鸣高冈之鸾凤。照胆秘诀云：用同卯贵两加户，意孟不足难守故。"));
        set(2, new LiuReKe("癸丑日第十二局　干上寅", "课体：元首，进茹，孤辰。", "课义：干支及传，脱空满前，子息耗盗，弱病淹延。", "解曰：干支三传皆脱干之神。又系旬空，昼将元武临身发用，木乃水之子息，定主子息耗盗。如占病必然虚脱之症，已入膏盲，虽越人无可如何也。", "断曰：元首之卦，事起男子，举动宜先。但干支上乘网刃，岂可前进。况进茹空亡，自宜退步。占主耗泄虚诈，凡事不实，无影无形，不足凭据，凶忧却可解散。", "天时：先风而后雨，未必大，家宅：宅替人衰。功名：官星值墓落陷受克，大不利。求财：满目空脱，财爻不见，无有。婚姻：传课皆空，不吉，亦不成。胎产：妊产皆不利。疾病：心肺两家之症，甚凶，新病有解。争讼：可以脱，然无害矣。谋望：总无成。出行：不利，以网刃在前也。行人：到处皆空，失意而归。失脱：不可寻觅。捕获：昼占即在左邻，同门中人作线。兵战：大不利。", "毕法云：进茹空亡宜退步，所谋多拙遭罗网，脱上逢脱防虚诈，空空如也事休追。六壬会通云：三传来生我，万事皆顺吉。若见我生传，何时得显迹。指掌赋云：寅卯辰为正和，展经略而果沐恩光。又云：顺连茹空，名曰声传空谷，退吉而进则不宜。又云：三传纯子孙，不求财而财自至。"));
    }
};
